"""
X402 Network Configuration
Centralized configuration for contract addresses and network settings
"""

from typing import Dict


class NetworkConfig:
    """Network configuration for contract addresses and chain IDs"""

    # Default networks
    TRON_MAINNET = "tron:mainnet"
    TRON_SHASTA = "tron:shasta"
    TRON_NILE = "tron:nile"

    # TRON Chain IDs
    CHAIN_IDS: Dict[str, int] = {
        "tron:mainnet": 728126428,  # 0x2b6653dc
        "tron:shasta": 2494104990,  # 0x94a9059e
        "tron:nile": 3448148188,  # 0xcd8690dc
    }

    # PaymentPermit contract addresses
    PAYMENT_PERMIT_ADDRESSES: Dict[str, str] = {
        "tron:mainnet": "THnW1E6yQWgx9P3QtSqWw2t3qGwH35jARg",
        "tron:shasta": "TVjYLoXatyMkemxzeB9M8ZE3uGttR9QZJ8",
        "tron:nile": "TQr1nSWDLWgmJ3tkbFZANnaFcB5ci7Hvxa",
    }

    # GasFree contract addresses
    GASFREE_CONTROLLER_ADDRESSES: Dict[str, str] = {
        "tron:mainnet": "TFFAMLQZybALab4uxHA9RBE7pxhUAjfF3U",
        "tron:shasta": "TQghdCeVDA6CnuNVTUhfaAyPfTetqZWNpm",
        "tron:nile": "THQGuFzL87ZqhxkgqYEryRAd7gqFqL5rdc",
    }

    GASFREE_BEACON_ADDRESSES: Dict[str, str] = {
        "tron:mainnet": "TSP9UW6FQhT76XD2jWA6ipGMx3yGbjDffP",
        "tron:shasta": "TQ1jvA3nLDMDNbJoMPLzTPoqAg8NvZ5CCW",
        "tron:nile": "TLtCGmaxH3PbuaF6kbybwteZcHptEdgQGC",
    }

    GASFREE_CREATION_CODES: Dict[str, str] = {
        "tron:mainnet": "",
        "tron:shasta": "",
        "tron:nile": "",
    }

    GASFREE_API_BASE_URLS: Dict[str, str] = {
        "tron:mainnet": "https://open.gasfree.io/tron",
        "tron:shasta": "https://open-test.gasfree.io/shasta",
        "tron:nile": "https://open-test.gasfree.io/nile",
    }

    @classmethod
    def get_chain_id(cls, network: str) -> int:
        """Get chain ID for network

        Args:
            network: Network identifier (e.g., "tron:nile", "tron:mainnet")

        Returns:
            Chain ID as integer

        Raises:
            ValueError: If network is not supported
        """
        chain_id = cls.CHAIN_IDS.get(network)
        if chain_id is None:
            raise ValueError(f"Unsupported network: {network}")
        return chain_id

    @classmethod
    def get_payment_permit_address(cls, network: str) -> str:
        """Get PaymentPermit contract address for network

        Args:
            network: Network identifier (e.g., "tron:nile", "tron:mainnet")

        Returns:
            Contract address in Base58 format
        """
        return cls.PAYMENT_PERMIT_ADDRESSES.get(network, "T0000000000000000000000000000000")

    @classmethod
    def get_gasfree_controller_address(cls, network: str) -> str:
        """Get GasFreeController contract address for network"""
        return cls.GASFREE_CONTROLLER_ADDRESSES.get(network, "T0000000000000000000000000000000")

    @classmethod
    def get_gasfree_beacon_address(cls, network: str) -> str:
        """Get GasFree beacon address for network"""
        return cls.GASFREE_BEACON_ADDRESSES.get(network, "T0000000000000000000000000000000")

    @classmethod
    def get_gasfree_creation_code(cls, network: str) -> str:
        """Get GasFree creation code for network"""
        return cls.GASFREE_CREATION_CODES.get(network, "")

    @classmethod
    def get_gasfree_api_base_url(cls, network: str) -> str:
        """Get GasFree API Base URL for network"""
        return cls.GASFREE_API_BASE_URLS.get(network, "https://open.gasfree.io/tron")

    @classmethod
    def get_gasfree_api_key(cls, _network: str) -> str | None:
        """Get GasFree API Key (usually from environment)"""
        import os

        return os.getenv("GASFREE_API_KEY")

    @classmethod
    def get_gasfree_api_secret(cls, _network: str) -> str | None:
        """Get GasFree API Secret (usually from environment)"""
        import os

        return os.getenv("GASFREE_API_SECRET")
